const program = require('commander');

program
  .version(require('../package').version)
  .option('-p, --specs-path [path]', 'path to a directory with spec files')
  .option('-v --verbose', 'print out more details during the process')
  .option('-f --force', 'force update all specs in petri server')
  .option('--dry-run', 'go through the process without creating/updating specs')
  .parse(process.argv);

require('../lib/petri-specs').publish({
  isDryRun: program.dryRun,
  specsPath: program.specsPath,
  force: program.force,
  isVerbose: program.verbose,
});
