const path = require('path');
const fs = require('fs');
const mkdirp = require('mkdirp');
const relativeToRoot = require('./utils/relative-to-root');
const readDirectory = require('./utils/read-directory');

module.exports = function (options) {
  const files = readDirectory(relativeToRoot('app/petri-experiments'));
  files.forEach(file => {
    mkdirp(options.directory);
    fs.renameSync(file, path.join(options.directory, path.basename(file)));
  });
};
