class PetriConfiguration {
  constructor(config) {
    this.config = config;
  }

  hasConfiguration() {
    return !!this.config;
  }

  _getFromConfig(prop, defaultVAlue) {
    return this.hasConfiguration() ? this.config[prop] : defaultVAlue;
  }

  getDefaultOwner() {
    return this._getFromConfig('owner', '');
  }

  getScopes() {
    return this._getFromConfig('scopes', []);
  }

  getDefaultScopes() {
    return this._getFromConfig('defaultScopes', []);
  }

  hasConfiguredScopes() {
    return this._getFromConfig('scopes', false);
  }
}

module.exports = PetriConfiguration;
